namespace ValidationControl.Images
{
    public static class ImageCollection
    {
        private static string ErrorIconBase64 =>
            "";

        public static ImageSource ErrorIcon =>
         ImageSource.FromStream(() => new MemoryStream(Convert.FromBase64String(ErrorIconBase64)));

        private static string ClearIconBase64 =>
            "";

        public static ImageSource ClearIcon =>
         ImageSource.FromStream(() => new MemoryStream(Convert.FromBase64String(ClearIconBase64)));

		private static string ShowPasswordIconBase64 =>
			"";

		public static ImageSource ShowPasswordIcon =>
		 ImageSource.FromStream(() => new MemoryStream(Convert.FromBase64String(ShowPasswordIconBase64)));
		private static string HidePasswordIconBase64 =>
			"";

		public static ImageSource HidePasswordIcon =>
		 ImageSource.FromStream(() => new MemoryStream(Convert.FromBase64String(HidePasswordIconBase64)));
	}
}
